import React from 'react';
 
const isLoggedIn = false;

const ExampleComponent = ({ isLoggedIn }) => (
  <div>
    {isLoggedIn ? (<p>Welcome, user!</p>) : (<p>Please log in to access the content.</p>)}
  </div>
);

export default ExampleComponent;


